import React, { useContext, useState } from "react"
import { AppContext } from "../contex/AppContext"




function DataCart(){
    const {dataContext} = useContext(AppContext)
    console.log(dataContext);
    

    function Xnxxxx(){
        const [amountCart,setAmountCart] = useState(0)
         
        return(
            <div> <button onClick={()=>{setAmountCart(amountCart+1)}}>+</button>{amountCart}<button onClick={()=>{setAmountCart(amountCart-1)}}>-</button> </div>
        )
    
    } 

    const DataCartV = dataContext.map(function(x){
    
       
        
       return ( <div className="contentCart">
       <div>ten: {x.name} , gia: {x.gia}VND, sl: {x.soluong}</div>
       <Xnxxxx/>
        
    </div>)
    })

           
        
     
     

   
    
    
    
   
    
    
   
    return( <div>
       {DataCartV}
       </div>
    )     
       
    
}
export default DataCart;
