import React ,{useState,useEffect} from "react"
import axios from 'axios'

function Post() {

  const [dataName,setDataName] = useState("");
  const [dataPrice,setDataPrice] = useState('');
  const [dataAmount,setDataAmount] = useState('');
  const [dataImage,setDataImage] = useState('');
 
  const SetImage =(event)=>{
    const file = event.target.files[0]
    console.log(file);
    
    
  }
  const fileReader = new FileReader()
  fileReader.readAsDataURL(file)
  fileReader.onloadend = (e)=>{
    setDataImage = e.target.result

  }
  
  function setName(event){
      setDataName(event.target.value)
      console.log(dataName,'name');
  };
  console.log(dataName);
 function setPrice(event){
    setDataPrice(event.target.value)
    console.log(dataPrice,'sl');
};
function setAmount(event){
    setDataAmount(event.target.value)
    console.log(dataAmount,'gia');
}
console.log(dataName,'name');
console.log(dataPrice,'sl');
console.log(dataAmount,'gia');
console.log(dataImage,'img');

  const newData = {
    name:dataName,
    gia:dataPrice,
    soluong:dataAmount,
    img:dataImage
}
console.log(newData,'newdata');

function postData(event){
  event.preventDefault()
  axios.post('http://localhost:5000/data?a=50',newData
  
  )
    .then(function (response) {
      console.log(response);
    })
    .catch(function (error) {
      console.log(error);
    });

}

  return(
       
       <div>
        <div>post</div>
        <form>
            <label>Name:</label>
            <input type="text"  
                   value={dataName}
                   onChange={setName} />
            
            <table>price:</table>
            <input type="text" 
                   value={dataPrice}
                   onChange={setPrice} />
            
            <table>amount:</table>
            <input type="numble"
                   value={dataAmount}
                   onChange={setAmount} />
            
            <table>img:</table>
            <input type="file"
                   
                   onChange={SetImage}   />
            
           
            <button onClick={postData}>Thêm mặt hàng</button>
        </form>
        <div>{newData.name} </div>
       </div>
    )
}

export default Post
