import React, { useContext, useState } from "react"
import { AppContext } from "../contex/AppContext"



function DataCart(){
    const {dataContext} = useContext(AppContext)
    console.log(dataContext);
    const dataXnxx = dataContext.map(function(x){
        return x
    })
    console.log(dataXnxx);
   
    function DataCartV (){
        const [amountCart,setAmountCart] = useState(1)
        
        for(let dataContextc of dataContext){
            return(
                <div className="contentCart">
                   <div>ten: {dataContextc.name} , gia: {dataContextc.gia}VND, sl: {dataContextc.soluong}</div>
                    <div> <button onClick={()=>{setAmountCart(amountCart+1)}}>+</button>{amountCart}<button>-</button> </div>
                </div>)
         }
       

       return
           
        
    } 
    DataCartV()
    
    
   
    return( <div>
       {DataCartV}
       </div>
    )     
       
    
}
export default DataCart;
