import React,{useContext, useEffect,useState} from "react"
import { AppContext } from "../contex/AppContext";
import { Link } from "react-router-dom";

import '../style/home.css'

function Home(props) {

   //console.log(props.data,'successfull connect data');
  const {dataContext,setDataContext,tongTien,setTongTien,setAmountCart,amountCart,setDataDetail,dataDetail} = useContext(AppContext)
  //console.log(dataContext);
  const AddToBasket = (x)=>{
      console.log(x.gia,'x')
      setDataContext(dataContext.concat(x))
      setTongTien(tongTien+x.gia)
      
      // them 1 key vo opjec --------------------------------------------------------------------------------------
      const zzzz={...amountCart};
      zzzz[x.name] = {sl:1}
      setAmountCart(zzzz)
      console.log(tongTien);
     }
    // console.log(dataContext);
  function detail(x){
    setDataDetail(x)
    console.log(x);
    console.log(dataDetail);
  }
  
  const ListData = props.data.map(function(x,index){
    return(
      <div className="contentCon1">
       <img className="img" src={x.img}></img>
       <div  className="name"> tên sản phẩm: {x.name}</div>
       <div className="old">giá: {x.gia}</div>
       <div className="soluong"> số lượng trong kho: {x.soluong}</div>
       <button onClick={()=>AddToBasket(x)}>thêm vào giỏ hàng</button>
       <Link to='#' onClick={()=>detail(x)}>chi tiet</Link>
      </div>  
    )
   });
   
  return(
  <div className="content">
    <h1>Gợi Ý Hôm Nay </h1>
    <div className="contentCon">
     {ListData}
    </div>
  </div>
)
}

export default Home;
