import React, { useContext, useState } from "react"
import { AppContext } from "../contex/AppContext"




function DataCart(){
    const {dataContext} = useContext(AppContext)
    console.log(dataContext);
    const {setTongTien,tongTien,amountCart,setAmountCart}= useContext(AppContext)
      
   

    function Xnxxxx(x){
        
        
        const gia = x.gia
        
        function increase(gia){
            
            setTongTien(tongTien+gia.gia)
            const name = gia.name

            if (amountCart.hasOwnProperty(name)) {
                const obj = {...amountCart}
                obj[name] = { sl: obj[name].sl + 1 }
                setAmountCart(obj)}
            console.log(amountCart);
            }
     
       
        
        return(
            <div> 
                <button onClick={() =>{increase(gia)}}>+</button>
                1
                <button onClick={()=>{}}>-</button> 
           </div>
        )
        
    
    } 

    const DataCartV = dataContext.map(function(x){
    
       
        
       return ( <div className="contentCart">
       <div>ten: {x.name} , gia: {x.gia}VND, sl: {x.soluong}</div>
       <Xnxxxx gia={x}/>
        
    </div>)
    })

           
        
     
     

   
    
    
    
   
    
    
   
    return( <div>
       {DataCartV}
       </div>
    )     
       
    
}
export default DataCart;
