  const {cartData,setCartData,dataContext,setDataContex,tongTien,setTongTien} = useContext(AppContext)
  const [amountCart,setAmountCart] = useState({});
  //const amountCartSl = amountCart.map()
  const DataCart = dataContext.map(function(x){

    const check = amountCart.hasOwnProperty(x.name) ? amountCart[x.name].amount : 1;
    
    return(
        <div className="contentCart">
               <div>ten: {x.name} , gia: {x.gia}VND, sl: {x.soluong}</div>
               <div> <button onClick={()=>{increase(x.gia,x._id, x.name);}}>+</button>{check}<button>-</button> </div>
            </div>
    ) 
  })
  console.log(DataCart);
  
  function increase(gia,_id, name){
    console.log(_id, name);
    setTongTien(tongTien+gia)
    if (amountCart.hasOwnProperty(name)) {
        const obj = {...amountCart}
