import  axios from'axios';
import React, { useState } from "react";




function Login(){

   const [account,setAccount] = useState();
   const [password,setPassword] = useState()
   const [dataAccount,setDataAccount] = useState()
   const [resInPost,setResInPost] = useState()
   
   
   
  
    console.log(account);
    console.log(password);
    const dataAcc ={acc:account,pass:password}
    function logIn(event){
       event.preventDefault()
        
        console.log(dataAcc);
        axios.post('http://localhost:5000/data/account?q=5',dataAcc)
                .then(function (response) {
                    console.log(response.data);
                    const resData = response.data
                    setResInPost(resData)
                    
                   
                    
                })
                .catch(function (error) {
                    console.log(error);
                });
        setAccount('')
        setPassword('')
        
        
    }
    console.log(resInPost);
    
//    function logOut(event){
//     setResInPost('')
//    }
   
    
    return(
        <div className="login">
            <form className='fromLogin'>
                <label>Account:
                    <input type='text'
                           value={account}
                           onChange={(e)=>{setAccount(e.target.value)}}
                    />
                </label>

                <label>Password:
                    <input type='password'
                           value={password}
                           onChange={(e)=>{setPassword(e.target.value)}}
                    />
                </label>
                <button onClick={logIn}>login</button>
                {/* <button onClick={logOut}>logout</button> */}
                {resInPost && resInPost.notification}
                {resInPost.data[0] && <div>so du tai khaon cua ban la: {resInPost.data[0].taikhoan}VND</div>}
                {resInPost.data[0] && <div>sdt cua ban la: {resInPost.data[0].sdt} 84+</div>}
                
            </form>
           
        </div>
    )

}

export default Login;
