import React,{useEffect,useState} from "react"
import '../style/home.css'

function Home(props) {

   console.log(props.data,'successfull connect data');

   const [basKets,setBaskets] = useState([])
   const AddToBasket = (event)=>{

    console.log('successfull implementation');
    setBaskets(1)
    console.log(basKets);
   }
   const ListData = props.data.map(function(x,index){
    console.log(x.name);
    console.log(x,'data');
    return(
      <div className="contentCon1">
        <img className="img" src={x.img}></img>
       <div  className="name"> tên sản phẩm: {x.name}</div>
       <div className="old">giá: {x.gia}</div>
       <div className="soluong"> số lượng trong kho: {x.soluong}</div>
       <button id={x.id} onClick={AddToBasket}>thêm vào giỏ hàng</button>
      </div>
      
    )
   });
  

  return(
  <div className="content">
    <h1>Gợi Ý Hôm Nay </h1>
    <div className="contentCon">
     {ListData}
    </div>
  </div>
)
}

export default Home;
