import React, { useContext, useState } from "react"
import { AppContext } from "../contex/AppContext"




function DataCart(){
    const {dataContext} = useContext(AppContext)
    console.log(dataContext);
    const {setTongTien,tongTien}= useContext(AppContext)

       
    function Xnxxxx(x){
        
        
        const [amountCart,setAmountCart] = useState(1)
         
        function Gia(x){
            
           setTongTien(tongTien+x.gia);
            setAmountCart(amountCart+2);
        }
        console.log(amountCart);
        return(
            <div> 
                <button onClick={() => Gia(x)}>+</button>
                {amountCart}
                <button onClick={()=>{setAmountCart(amountCart-1)}}>-</button> 
           </div>
        )
        
    
    } 

    const DataCartV = dataContext.map(function(x){
    
       
        
       return ( <div className="contentCart">
       <div>ten: {x.name} , gia: {x.gia}VND, sl: {x.soluong}</div>
       <Xnxxxx gia={x.gia}/>
        
    </div>)
    })

           
        
     
     

   
    
    
    
   
    
    
   
    return( <div>
       {DataCartV}
       </div>
    )     
       
    
}
export default DataCart;
