import React,{useContext, useEffect,useState} from "react"
import { AppContext } from "../contex/AppContext";

import '../style/home.css'

function Home(props) {

   //console.log(props.data,'successfull connect data');
   const {dataContext,setDataContext,tongTien,setTongTien,setAmountCart} = useContext(AppContext)

   //console.log(dataContext);
    const AddToBasket = (x)=>{
    
      
      console.log(x.gia,'x')
      
      setDataContext(dataContext.concat(x))
      setTongTien(tongTien+x.gia)
      setAmountCart({name:x.name,sl:1})
      console.log(tongTien);
      
     }
    // console.log(dataContext);
   
 
  
   const ListData = props.data.map(function(x,index){
    
    
    return(
      
      <div className="contentCon1">
       <img className="img" src={x.img}></img>
       <div  className="name"> tên sản phẩm: {x.name}</div>
       <div className="old">giá: {x.gia}</div>
       <div className="soluong"> số lượng trong kho: {x.soluong}</div>
       <button onClick={()=>AddToBasket(x)}>thêm vào giỏ hàng</button>
      </div>
      
      
    )
   });
   
  

  return(
  <div className="content">
    <h1>Gợi Ý Hôm Nay </h1>
    <div className="contentCon">
     {ListData}
    </div>
  </div>
)
}

export default Home;
