import  axios from'axios';
import React, { useContext, useState } from "react";
import { AppContext } from '../contex/AppContext';
import '../style/login.css'




function Login(){

   const [account,setAccount] = useState();
   const [password,setPassword] = useState()
   const [dataAccount,setDataAccount] = useState()
   const [resInPost,setResInPost] = useState('')
   const [taikhoanUser,setTaikhoanUser] = useState()
   const [sdtUser,setSdtUser] = useState()
   const {dataLogIn,setDataLogIn} = useContext(AppContext)
   
   
  
    console.log(account);
    console.log(password);
    const dataAcc ={acc:account,pass:password}
    function login(event){
       event.preventDefault()
        
        console.log(dataAcc);
        axios.post('http://localhost:5000/data/account?q=5',dataAcc)
                .then(function (response) {
                    console.log(response.data);
                    const resData = response.data
                    setResInPost(resData)
                    if(resData){
                        setDataLogIn(1)
                    }
                    
                   
                    
                })
                .catch(function (error) {
                    console.log(error);
                });
        setAccount('')
        setPassword('')
        
        
    }
    console.log(resInPost);
    
    function logout(event){
        event.preventDefault()
        setResInPost({notification:'vui long dang nhap lai'})
        
    }
    console.log(resInPost);
   
    
    return(
        <div className="login">
            <form className='fromLogin'>
                <label>Account:
                    <input type='text'
                           value={account}
                           onChange={(e)=>{setAccount(e.target.value)}}
                    />
                </label><br/>

                <label>Password:
                    <input type='password'
                           value={password}
                           onChange={(e)=>{setPassword(e.target.value)}}
                    />
                </label>
                 <div className='loginButton'>
                <div> <button onClick={login}>login</button></div>
                <div> <button onClick={logout}>logout</button></div>
                </div>
               
                
            </form>
            {resInPost && resInPost.notification}
            {resInPost.data && <div>so du tai khaon cua ban la: {resInPost.data[0].taikhoan}VND</div>}
            {resInPost.data && <div>sdt cua ban la: {resInPost.data[0].sdt}SDT</div>}
           
        </div>
    )

}

export default Login;
