import './App.css';
import axios from 'axios'
import {Routes,Route,Link} from "react-router-dom"
import React,{useContext, useEffect,useState} from 'react';
import Home from './pages/home';
import New from './pages/New';
import Post from './pages/post';
import imgv from './img/th.jpg';
import frame from './img/frame.jpg';
import Cart from './cart/cart';
import { AppContext, AppProvider } from './contex/AppContext';


function App() {
  const [data,setData] = useState([]);

  useEffect(()=>{
    axios.get('http://localhost:5000/data')

    .then((res)=>{
      const getData = res.data;
     // console.log('----------',getData);
      setData(getData)
    },)

  },[])
 //console.log(data);
 const {dataContext,setDataContext} = useContext(AppContext)
 console.log(dataContext);
  const dataBaskets = dataContext.map(function(x,index){
    console.log(x);
   return x
  })

  return (

    <div className='App'>

       <div className='hearder'>

 <nav className='headerTop1'>
          <ul className='ul1'>
            <li>
              <Link to='/'><i className="fa-solid fa-house"></i>HOME</Link>
            </li>
            <li>
              <Link to='/new'>NEW</Link>
            </li>
            <li>
              <Link to='/post'>POST</Link>
            </li>
          </ul>
          <ul className='ul2'>
            <li>
              <a href='#'> <i className="fa-solid fa-bell"></i> Thông Báo</a>
            </li>
            <li>
              <a href='#'> <i className="fa-regular fa-circle-question"></i> Hỗ Trợ</a>
            </li>
            <li>
              <a href='#'>Đăng ký</a>
            </li>
            <li>
              <a href='#'>Đăng nhập</a>

            </li>
          </ul>
        </nav>

          <div className='hearderBotton'>
            <img src={imgv}></img>
            <div>
             <input type='text' placeholder='tim kiem'></input>
             <i className="fa-solid fa-magnifying-glass"></i>
            </div>
            <div>
            <a href='#'><i className="fa-solid fa-cart-shopping">{dataContext.length}</i></a>
            <Cart></Cart>
            </div>
          </div>
      </div>
      <div className='content'>
        <div className='lerftSidebar'>
        <Routes>
        <Route path='/' element={<Home data={data}/>}/>

        <Route path='/new' element={<New />}/>

        <Route path='/post' element={<Post data={data}/>}/>
       </Routes>
        </div >
        <div className='rightSidebar'>sight Sidebar</div>
        <div> <img src={frame}></img></div>

    </div>


    </div>

  );
}

export default App;
