import  axios from'axios';
import React, { useState } from "react";




function Login(){

   const [account,setAccount] = useState();
   const [password,setPassword] = useState()
   const [dataAccount,setDataAccount] = useState()
   const [resInPost,setResInPost] = useState()
   const [taikhoanUser,setTaikhoanUser] = useState()
   const [sdtUser,setSdtUser] = useState()
   
   
  
    console.log(account);
    console.log(password);
    const dataAcc ={acc:account,pass:password}
    function dangnhap(event){
       event.preventDefault()
        
        console.log(dataAcc);
        axios.post('http://localhost:5000/data/account?q=5',dataAcc)
                .then(function (response) {
                    console.log(response.data);
                    const resData = response.data
                    setResInPost(resData)
                    
                   
                    
                })
                .catch(function (error) {
                    console.log(error);
                });
        setAccount('')
        setPassword('')
        
        
    }
    console.log(resInPost);
    console.log(resInPost.data[0]);
  
    function DataP(x){
        if(resInPost.data[0]){
            const datap1 = resInPost.data[0]
            DataP(datap1)
            
        }
        return(<div>
            <div>so du tai khoan cua ban la:{x.taikhoan}</div>
            <div>sdt cua ban la: {x.sdt}</div>
            </div>
        )
    }
    return(
        <div className="login">
            <form className='fromLogin'>
                <label>Account:
                    <input type='text'
                           value={account}
                           onChange={(e)=>{setAccount(e.target.value)}}
                    />
                </label>

                <label>Password:
                    <input type='password'
                           value={password}
                           onChange={(e)=>{setPassword(e.target.value)}}
                    />
                </label>
                <button onClick={dangnhap}>dang nhap</button>
                {resInPost && resInPost.notification}
                    
                
            </form>
           
        </div>
    )

}

export default Login;
