import React ,{useState,useEffect} from "react"
import axios from 'axios'
import { raw } from "express";

function Post() {

  let [dataName,setDataName] = useState("");
  let [dataPrice,setDataPrice] = useState('');
  let [dataAmount,setDataAmount] = useState('');
 
  
  function setName(event){
      setDataName(event.target.value)
      console.log(dataName,'name');
  };
  console.log(dataName);
 function setPrice(event){
    setDataPrice(event.target.value)
    console.log(dataPrice,'sl');
};
function setAmount(event){
    setDataAmount(event.target.value)
    console.log(dataAmount,'gia');
}
console.log(dataName);
console.log(dataPrice,'sl1');
console.log(dataAmount,'gia1');
let newData = {
    name:dataName,
    gia:dataPrice,
    soluong:dataAmount
}
console.log(newData,'newdata');
function PostData(event){
    event.preventDefault()
   
    axios.post('http://localhost:5000/data?a=50',newData
    
    )
      .then(function (response) {
        console.log(response);
      })
      .catch(function (error) {
        console.log(error);
      });
      return <div>{newData.name}--------{newData.gia} </div>

}
  return(
       
       <div>
        <div>post</div>
        <form>
            <label>Name:</label>
            <input type="text"  
                   value={dataName}
                   onChange={setName} />
            
            <table>price:</table>
            <input type="text" 
                   value={dataPrice}
                   onChange={setPrice} />
            
            <table>amount:</table>
            <input type="numble"
                   value={dataAmount}
                   onChange={setAmount} />
            
            <table>img:</table>
            <input type="file" />
            
           
            <button onClick={PostData}>Thêm mặt hàng</button>
        </form>
        <PostData/>
       </div>
    )
}

export default Post
