import  axios from'axios';
import React, { useState } from "react";




function Login(){

   const [account,setAccount] = useState();
   const [password,setPassword] = useState()
   const [dataAccount,setDataAccount] = useState()
   const [resInPost,setResInPost] = useState()
   const [taikhoanUser,setTaikhoanUser] = useState()
   const [sdtUser,setSdtUser] = useState()
   
   
  
    console.log(account);
    console.log(password);
    const dataAcc ={acc:account,pass:password}
    function dangnhap(event){
       event.preventDefault()
        
        console.log(dataAcc);
        axios.post('http://localhost:5000/data/account?q=5',dataAcc)
                .then(function (response) {
                    console.log(response.data);
                    const resData = response.data
                    setResInPost(resData)
                    console.log(resData.notification);
                    console.log(resInPost.data);
                    
                })
                .catch(function (error) {
                    console.log(error);
                });
        setAccount('')
        setPassword('')
        
        
    }
    if(resInPost.data) {const dataUserTk = resInPost.data.map(function(x){
           setTaikhoanUser(x.taihoan)
        return x
    })}else{console.log('ko');}
    if(resInPost.data) {const dataUserSdt = resInPost.data.map(function(x){
        setTaikhoanUser(x.sdt)
     return x
    })}else{console.log('ko');}
    
    return(
        <div className="login">
            <form className='fromLogin'>
                <label>Account:
                    <input type='text'
                           value={account}
                           onChange={(e)=>{setAccount(e.target.value)}}
                    />
                </label>

                <label>Password:
                    <input type='password'
                           value={password}
                           onChange={(e)=>{setPassword(e.target.value)}}
                    />
                </label>
                <button onClick={dangnhap}>dang nhap</button>
                
                {resInPost.data && resInPost.notification}
                
            </form>
           
        </div>
    )

}

export default Login;
